#include <iostream>
#include <fstream>
using namespace std;

int main() {
    // فتح الملف النصي
    ifstream file("data.txt");
    // التأكد من أن الملف قد تم فتحه بنجاح
    if (file.is_open()) {
        // قراءة الملف سطرًا بسطر
        string line;
        while (file >> line) {
            // الحصول على عدد المرات التي تظهر فيها الكلمة المحددة
            int count = 0;
            string wordToFind = "wordToFind";
            string::find(line, wordToFind, count);
            cout << "The word " << wordToFind << " appears " << count << " times in the file." << endl;
        }
        file.close();
    } else {
        cout << "Unable to open file." << endl;
    }
    return 0;
}
