```c++
#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <algorithm>
using namespace std;
int main() {
    string word;
    int count = 0;
    ifstream file("file.txt");
    vector<string> lines;
    string line;
    while (getline(file, line)) {
        lines.push_back(line);
    }
    file.close();
    for (string line : lines) {
        for (int i = 0; i < line.length(); i++) {
            if (line[i] == ' ') {
                word = line.substr(0, i);
                count++;
            }
        }
    }
    cout << "The word " << word << " appears " << count << " times in the file." << endl;
    return 0;
}
```

